import React from "react";
import { headerIcons, headerTabs } from "src/constants/general";

const Header = () => {
  const path = window.location.pathname;

  return (
    <aside className="flex items-center justify-between gap-10 px-7 py-6 w-full text-2xl tracking-widest font-bold">
      <a href="/">KAIXIN </a>
      <article className="flex items-center gap-20">
        {headerTabs.map((tab) => {
          return (
            <a
              key={tab.name}
              href={tab.link}
              className={`uppercase ${
                path === tab.link ? "" : "text-gray-500 opacity-10"
              }`}
            >
              {tab.name}
            </a>
          );
        })}
      </article>
      <article className="flex items-center gap-5">
        {headerIcons.map((icon) => {
          return (
            <a
              key={icon.name}
              href={icon.link}
              target="_blank"
              rel="noopener noreferrer"
            >
              <img
                src={`/${icon.name}.png`}
                alt={icon.name}
                className="w-10 h-10"
              />
            </a>
          );
        })}
      </article>
    </aside>
  );
};

export default Header;
