import React from "react";
import PageLayout from "src/Layouts/PageLayout";
import { projects } from "src/constants/projects";

const Work = () => {
  const path = window.location.pathname;

  const project = projects[path];

  return (
    <PageLayout>
      {project && (
        <main className="flex flex-col flex-grow gap-20 px-32 mx-10 mb-10 border-x-2 border-black overflow-auto">
          <h4 className="grid py-20 text-8xl tracking-wider font-medium border-b border-black">
            {project.name}
          </h4>
          <p>{project.description}</p>
          <img src={`/projects/${project.screenshot}`} alt="" />
          <ul className="grid grid-flow-col mr-0 mx-auto gap-2 items-center">
            {project.tools.map((tool: any, idx: number) => {
              return (
                <li key={idx} className="">
                  <Image src={tool} alt="tool" height={30} width={30}></Image>
                </li>
              );
            })}
          </ul>
          <article className="grid grid-cols-2 justify-self-start d:justify-self-end text-center text-xs t:text-sm tracking-widest gap-3">
            <a
              href={project.demo}
              target="_blank"
              rel="noopener noreferrer"
              className={`${
                project.name === "Trello Clone" && "hidden"
              } px-5 py-2 font-semibold hover:font-extrabold bg-red-300 hover:bg-red-100 hover:ring-2 hover:ring-red-300 hover:text-red-300 shadow-lg shadow-red-300 rounded-md`}
            >
              LIVE
            </a>
            <a
              href={project.code}
              target="_blank"
              rel="noopener noreferrer"
              className={`${
                project.name === "Trello Clone" && "d:col-start-2"
              } px-5 py-2 font-semibold hover:font-extrabold bg-red-300 hover:bg-red-100 hover:ring-2 hover:ring-red-300 hover:text-red-300 shadow-lg shadow-red-300 rounded-md`}
            >
              CODE
            </a>
          </article>
        </main>
      )}
    </PageLayout>
  );
};

export default Work;
